mapper.select:
select 

from route
where r_id = #{rId,jdbcType=INTEGER}
  and f_id = #{fId,jdbcType=INTEGER}
  and v_id = #{vId,jdbcType=INTEGER}

mapper.delete:
delete from route
where r_id = #{rId,jdbcType=INTEGER}
  and f_id = #{fId,jdbcType=INTEGER}
  and v_id = #{vId,jdbcType=INTEGER}

mapper.update:
update route


    route = #{route,jdbcType=VARCHAR},


where r_id = #{rId,jdbcType=INTEGER}
  and f_id = #{fId,jdbcType=INTEGER}
  and v_id = #{vId,jdbcType=INTEGER}

update route
set route = #{route,jdbcType=VARCHAR}
where r_id = #{rId,jdbcType=INTEGER}
  and f_id = #{fId,jdbcType=INTEGER}
  and v_id = #{vId,jdbcType=INTEGER}
